var s = 10;
const u = new URLSearchParams(window.location.search);
const si = u.get('session')?.replaceAll(':', '');

const SetRole = function () {
    const d = {
        confirm_reauthorize: true,
        reauthorize_profile: 'Final_Role [authenticated]'
    };
    const url = 'https://erfreewifi.com/api/session/' + si + '/reauthorize';
    $.ajax({
        type: 'Post',
        url: url,
        data: JSON.stringify(d),
        headers: {
            Authorization: 'Bearer df2766718c23c57bc43c082197de984b6d327b89',
            'Content-Type': 'application/json'
        },
        contentType: 'application/json; charset=utf-8',
        Accept: '*/*',
        success: function (res) {
            console.log('res:', res);
        },
        error: function (error) {
            console.log('error: ', error);
        }
    });
};

const t = setInterval(function () {
    document.querySelector('#counter').textContent = --s;
    // s === 3 && SetRole();
    s < 1 &&
        (clearInterval(t),
            (document.querySelector('#counter').textContent = ' إنهاء '),
            (window.location.href = 'form.html' + '?session=' + si));
}, 1000);
